contracts/access/Roles.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

abstract contract Roles is AccessControl {
    bytes32 public constant ROLE_ADMIN = keccak256("ROLE_ADMIN");
    bytes32 public constant ROLE_RISK = keccak256("ROLE_RISK");
    bytes32 public constant ROLE_ORACLE = keccak256("ROLE_ORACLE");
    bytes32 public constant ROLE_KEEPER = keccak256("ROLE_KEEPER");
    bytes32 public constant ROLE_PAUSER = keccak256("ROLE_PAUSER");

    constructor(address admin) {
        _grantRole(ROLE_ADMIN, admin);
        _setRoleAdmin(ROLE_RISK, ROLE_ADMIN);
        _setRoleAdmin(ROLE_ORACLE, ROLE_ADMIN);
        _setRoleAdmin(ROLE_KEEPER, ROLE_ADMIN);
        _setRoleAdmin(ROLE_PAUSER, ROLE_ADMIN);
    }
}

contracts/core/CollateralVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Pausable} from "@openzeppelin/contracts/utils/Pausable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ILoanManager} from "../interfaces/ILoanManager.sol";
import {IVault} from "../interfaces/IVault.sol";
import {Roles} from "../access/Roles.sol";

contract CollateralVault is IVault, Roles, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    IERC20 public immutable asset; // e.g., LSTBTC
    mapping(address => uint256) public balances;
    ILoanManager public loanManager; // optional backref for LTV checks/seize

    constructor(address admin, IERC20 _asset) Roles(admin) {
        asset = _asset;
    }

    function setLoanManager(ILoanManager _loan) external onlyRole(ROLE_RISK) {
        loanManager = _loan;
    }

    function pause() external onlyRole(ROLE_PAUSER) { _pause(); }
    function unpause() external onlyRole(ROLE_PAUSER) { _unpause(); }

    function deposit(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "amount");
        asset.safeTransferFrom(msg.sender, address(this), amount);
        balances[msg.sender] += amount;
        emit DepositCollateral(msg.sender, amount);
    }

    function withdraw(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0 && amount <= balances[msg.sender], "amount");
        // If linked to a LoanManager, enforce post-withdraw health (approx by proportionality)
        if (address(loanManager) != address(0)) {
            uint256 bal = balances[msg.sender];
            uint256 newCol = bal - amount;
            (, uint256 debt, uint256 hf) = loanManager.getAccountData(msg.sender);
            if (debt > 0) {
                // newHF ~= hf * (newCollateral / oldCollateral)
                require(bal > 0, "collateral");
                uint256 newHf = (hf * newCol) / bal;
                require(newHf >= 1e18, "hf");
            }
        }
        balances[msg.sender] -= amount;
        asset.safeTransfer(msg.sender, amount);
        emit WithdrawCollateral(msg.sender, amount);
    }

    function balanceOf(address user) external view returns (uint256) {
        return balances[user];
    }

    // Seize function for liquidations authorized by keeper role
    function seize(address user, address to, uint256 amount) external onlyRole(ROLE_KEEPER) nonReentrant {
        require(amount > 0 && amount <= balances[user], "amount");
        balances[user] -= amount;
        asset.safeTransfer(to, amount);
    }

    // helper removed (not needed in simplified guard)
}

contracts/core/LiquidationModule.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Roles} from "../access/Roles.sol";
import {LoanManager} from "./LoanManager.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {CollateralVault} from "./CollateralVault.sol";

contract LiquidationModule is Roles {
    using SafeERC20 for IERC20;
    LoanManager public immutable loanManager;
    uint256 public incentiveBps = 1000; // 10%
    IERC20 public immutable debtAsset;
    CollateralVault public immutable collateralVault;

    event Liquidate(address indexed user, uint256 repayAmount, uint256 collateralSeized, uint256 incentive);

    constructor(address admin, LoanManager _loanManager) Roles(admin) {
        loanManager = _loanManager;
        debtAsset = _loanManager.debtAsset();
        collateralVault = CollateralVault(address(_loanManager.collateralVault()));
    }

    function setIncentive(uint256 bps) external onlyRole(ROLE_RISK) { require(bps <= 2000, "bps"); incentiveBps = bps; }

    function liquidate(address user, uint256 repayAmount) external onlyRole(ROLE_KEEPER) {
        (, uint256 debt, uint256 hf) = loanManager.getAccountData(user);
        require(hf < 1e18, "healthy");
        require(repayAmount > 0 && repayAmount <= debt, "amount");
        // Transfer repay from keeper to this module, then approve LoanManager and repay on user's behalf
        debtAsset.safeTransferFrom(msg.sender, address(this), repayAmount);
        IERC20(address(debtAsset)).forceApprove(address(loanManager), 0);
        IERC20(address(debtAsset)).forceApprove(address(loanManager), repayAmount);
        loanManager.repayFrom(address(this), user, repayAmount);

        // Compute collateral to seize = repayAmount valued at oracle + incentiveBps premium
        // Para simplificar demo: usamos 1:1 (asumimos debtAsset y colateral con precio 1e18 equivalentes)
        uint256 baseSeize = repayAmount;
        uint256 incentive = (repayAmount * incentiveBps) / 10_000;
        uint256 totalSeize = baseSeize + incentive;
        // Move collateral from user to keeper
        collateralVault.seize(user, msg.sender, totalSeize);
        emit Liquidate(user, repayAmount, totalSeize, incentive);
    }
}

contracts/core/LoanManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Pausable} from "@openzeppelin/contracts/utils/Pausable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Roles} from "../access/Roles.sol";
import {IVault} from "../interfaces/IVault.sol";
import {ILoanManager} from "../interfaces/ILoanManager.sol";
import {IOracle} from "../interfaces/IOracle.sol";

contract LoanManager is Roles, Pausable, ReentrancyGuard, ILoanManager {
    using SafeERC20 for IERC20;
    IERC20 public immutable debtAsset; // USDT
    IVault public immutable collateralVault;
    IOracle public oracle; // optional
    address public collateralToken; // for oracle lookup

    uint256 public targetLtv = 6000; // bps 60%
    uint256 public liquidationLtv = 7500; // bps 75%
    uint256 public baseRateBps = 500; // 5%
    uint256 public interestIndex = 1e18; // 1.0 in 1e18
    uint256 public lastAccrual;

    mapping(address => uint256) public principalDebt; // unscaled principal per user
    mapping(address => uint256) public userIndex; // last index applied to user's debt

    event UpdateRates(uint256 baseRateBps);
    event AccrueInterest(uint256 newIndex, uint256 dt);

    constructor(address admin, IERC20 _debtAsset, IVault _vault) Roles(admin) {
        debtAsset = _debtAsset;
        collateralVault = _vault;
        lastAccrual = block.timestamp;
    }

    function setOracle(IOracle _oracle, address _collateralToken) external onlyRole(ROLE_RISK) {
        oracle = _oracle;
        collateralToken = _collateralToken;
    }

    function setParams(uint256 _target, uint256 _liq, uint256 _baseRate) external onlyRole(ROLE_RISK) {
        require(_target < _liq && _liq <= 9000, "params");
        targetLtv = _target; liquidationLtv = _liq; baseRateBps = _baseRate;
        emit UpdateRates(baseRateBps);
    }

    function borrow(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "amount");
        _applyInterest(msg.sender);
        (uint256 c, uint256 d, ) = getAccountData(msg.sender);
        uint256 newDebt = d + amount;
        require(_ltv(c, newDebt) <= targetLtv, "ltv");
        // update user principal using current index
        principalDebt[msg.sender] = _toPrincipal(newDebt);
        userIndex[msg.sender] = interestIndex;
        debtAsset.safeTransfer(msg.sender, amount);
        emit Borrow(msg.sender, amount);
    }

    function repay(uint256 amount) external nonReentrant whenNotPaused {
        _repayFrom(msg.sender, msg.sender, amount);
    }

    function repayFrom(address payer, address user, uint256 amount) external nonReentrant whenNotPaused {
        _repayFrom(payer, user, amount);
    }

    function _repayFrom(address payer, address user, uint256 amount) internal {
        require(amount > 0, "amount");
        _applyInterest(user);
        debtAsset.safeTransferFrom(payer, address(this), amount);
        uint256 d = _debtOf(user);
        uint256 newDebt = amount >= d ? 0 : d - amount;
        principalDebt[user] = _toPrincipal(newDebt);
        userIndex[user] = interestIndex;
        emit Repay(user, amount);
    }

    function accrueInterest() external whenNotPaused {
        uint256 dt = block.timestamp - lastAccrual;
        if (dt == 0) return;
        // rate per second in 1e18 = (baseRateBps/10000) / secondsPerYear
        uint256 ratePerSecond = (baseRateBps * 1e14) / 31536000; // 1e18 scale
        // simple linear approximation: index *= (1 + ratePerSecond * dt)
        uint256 increment = (interestIndex * ratePerSecond * dt) / 1e18;
        interestIndex = interestIndex + increment;
        lastAccrual = block.timestamp;
        emit AccrueInterest(interestIndex, dt);
    }

    function getAccountData(address user) public view returns (uint256 collateral, uint256 _debt, uint256 healthFactor) {
        collateral = collateralVault.balanceOf(user);
        _debt = _debtOf(user);
        if (_debt == 0) return (collateral, 0, type(uint256).max);
        // Value collateral in debt units if oracle is set (price scaled 1e18). Otherwise assume 1:1
        uint256 collateralUsd = collateral;
        if (address(oracle) != address(0) && collateralToken != address(0)) {
            (uint256 price, ) = oracle.getPrice(collateralToken);
            // collateral * price / 1e18
            collateralUsd = (collateral * price) / 1e18;
        }
        uint256 maxDebt = (collateralUsd * liquidationLtv) / 10_000;
        healthFactor = (maxDebt * 1e18) / _debt;
    }

    function debtOf(address user) external view returns (uint256) {
        return _debtOf(user);
    }

    function _debtOf(address user) internal view returns (uint256) {
        uint256 p = principalDebt[user];
        if (p == 0) return 0;
        uint256 idx = interestIndex;
        uint256 uidx = userIndex[user];
        if (uidx == 0) uidx = 1e18;
        // scaled debt = principal * (idx / uidx)
        return (p * idx) / uidx;
    }

    function _toPrincipal(uint256 scaledDebt) internal view returns (uint256) {
        // principal = scaled / (idx / 1e18) = scaled * 1e18 / idx, but idx is 1e18-based so simplify
        if (scaledDebt == 0) return 0;
        return (scaledDebt * 1e18) / interestIndex;
    }

    function _applyInterest(address user) internal {
        // noop: index accrual is global; this keeps slot updated for user on state changes
        if (userIndex[user] == 0) {
            userIndex[user] = interestIndex;
        }
    }

    function _ltv(uint256 collateral, uint256 _debt) internal view returns (uint256) {
        if (collateral == 0) return type(uint256).max;
        uint256 collateralUsd = collateral;
        if (address(oracle) != address(0) && collateralToken != address(0)) {
            (uint256 price, ) = oracle.getPrice(collateralToken);
            collateralUsd = (collateral * price) / 1e18;
        }
        return (_debt * 10_000) / collateralUsd;
    }
}

